import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { Grid, Container, Table, Message, Button } from 'semantic-ui-react'
import Menu from '../../components/header';
import { listCache, setCache } from '../../services/Resource';
import { useToasts } from 'react-toast-notifications'

const Cart = () => {

    const { addToast } = useToasts();
    const [ products, setProducts ] = useState([]);

    useEffect(() => {
        setProducts(listCache('storageCoffee'));
    }, []);

    const handleRemove = (id) => {
        const newStorage = products.filter( item => item.id !== id);
        setCache('storageCoffee', [...newStorage]);
        setProducts([...newStorage]);
        addToast('Item removido de sua sacola', { appearance: 'success', autoDismiss: true });
    };

    return (
        <Page>
            <Menu page="coffee" />
            <Container>
                <Grid columns='equal' className="content-inner">         
                    <Grid.Column>
                        { products.length > 0 ?                        
                            <Table color={'brown'}>
                                <Table.Header>
                                <Table.Row>
                                    <Table.HeaderCell></Table.HeaderCell>
                                    <Table.HeaderCell></Table.HeaderCell>
                                    <Table.HeaderCell>Quantidade</Table.HeaderCell>
                                    <Table.HeaderCell>Preço</Table.HeaderCell>
                                    <Table.HeaderCell></Table.HeaderCell>
                                </Table.Row>
                                </Table.Header>

                                <Table.Body>
                                    { products &&
                                        products.map( item => 
                                            <Table.Row>
                                                <Table.Cell style={{ textAlign: 'center'}}><img src={item['image_thumb']} alt="" height="76" /></Table.Cell>
                                                <Table.Cell>{item.name}</Table.Cell>
                                                <Table.Cell>1</Table.Cell>
                                                <Table.Cell>{`R$ ${item.price}`}</Table.Cell>
                                                <Table.Cell><Button circular icon="trash alternate outline" onClick={() => handleRemove(item.id)} /></Table.Cell>
                                            </Table.Row>  
                                        )
                                    }
                                </Table.Body>
                            </Table>

                            :

                            <Message visible>Sua sacola está vazia</Message>
                        }
                    </Grid.Column>         
                </Grid>
            </Container>
        </Page>
    )
};

export default Cart;

const Page = styled.section`
    .content-inner {
        margin-top: 20px;
    }
`
