import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import { Grid, Container, Header, Button } from 'semantic-ui-react'
import Menu from '../../components/header';
import { fetchData } from '../../services/Resource';

const Accessory = () => {

    const [ data, setData ] = useState([]);

    useEffect(() => {
        fetchData('products/category/accessories').then(res => {
          const response = res.data;
          setData(response.product);
        });
    }, []);

    const handleSubmit = (product) => {
        console.log(product)
    };

    return (
        <Page>
            <Menu page="accessory" />
            <Container>
                <Grid columns='equal' className="content-inner">

                    {
                        data.map( (item, index) =>
                            <Grid.Column width={4} key={index}>
                                <div>
                                    <Button circular icon='add to cart' color="orange" size="large" floated='right' onClick={() => handleSubmit(item)} />
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'center', clear: 'both' }}>
                                    <img className="coffee-thumb" src={item['image_thumb']} alt="" />
                                </div>
                                <Header as='h4'>{item.name}</Header>
                                <p>
                                    <span className="old-price">{item['old_price']}</span>
                                    <br />
                                    <span className="price">{item.price}</span>
                                </p>
                            </Grid.Column>
                        )
                    }

                </Grid>
            </Container>
        </Page>
    )
};

export default Accessory;

const Page = styled.section`
    .content-inner {
        margin-top: 20px;
    }
    .coffee-thumb {
        height: 190px;
    }
    .old-price {
        color: #9e9e9e;
        font-size: .9rem;
        font-weight: 400;
        padding-right: .5rem;
        text-decoration: line-through;
    }
    .price {
        color: black;
        font-size: 1.3rem;
        font-weight: bold;
    }
`
